// function for sending web requests

function apiRequest(endpoint, success_function, error_function = null){
    if (error_function === null){
        error_function = function(r){
            console.log("ERROR");
            console.log(r)
        }
    }
    $.ajax({
        type:"GET",
        url:"api/"+endpoint,
        success:function(r){
            if (r.interlock.status === "ok"){
                success_function(r);
            } else {
                error_function(r);
            }
        },
        error:error_function
    })
}

// function for creating a menu screen that overlays the current one

function createOverlay(title, html, success){
    let final_html = '<div class="overlay_content"><div class="overlay_header"><h1>'+title+'</h1></div>'+html+'</div>',
        container = $(".overlay_container");
    container.html(final_html);
    success();
    container.show()
}

// hides the overlay

function hideOverlay(){
    $(".overlay_container").hide();
}

// loads all the rooms

function loadRooms(){
    apiRequest("rooms", function(r){
        let rooms = r.rooms;
        updateRoomSelector(rooms);
        for (let i in rooms){
            let room = rooms[i];
            interlock_content.rooms.push(room);
            if (room.main){
                loadRoom(room);
            }
        }
    })
}

// loads a room

function loadRoom(room_content){

    interlock_content.current_room = room_content.id; // sets the current room to the room id

    $("#room_name").html(room_content.label); // loads the room name

    $(".room_item.selected").removeClass("selected"); // removes all the active classes

    $(".room_item[data-room_id='"+room_content.id+"']").addClass("selected"); // sets the room to selected from the room id

    updateSceneSelection(room_content.scenes); // sets the scenes from the interlock_content variable

    if (typeof room_content.current_scene === "undefined"){
        room_content.current_scene = "off";
    }

    updateSceneSelector(room_content.current_scene); // sets the button click events and sets the selected scene

    updateDevices(room_content.devices);

}

// updates the room selector

function updateRoomSelector(rooms){
    let room_html = '';
    // creates the html
    for (let i in rooms){
        let room = rooms[i];
        let svg = icon_svgs[room.svg];
        room_html += '<li class="room_item" data-room_id="'+room.id+'">'+svg+room.label+'</li>';
    }
    $("#room_selector").html(room_html);
    // creates the click events
    $("#room_selector > li").click(function(){
        let room_id = $(this).data("room_id"); // gets the room id
        for (let i in interlock_content.rooms){
            let room_content = interlock_content.rooms[i]; // gets the room content
            if (room_content.id === room_id){
                loadRoom(room_content); // loads the room
            }
        }
    });
}

// updates the devices from a room

function updateDevices(devices){
    let devices_html = "";
    for (let i in devices){
        let device = devices[i],
            on = false,
            state = '',
            icon = '',
            has_expand = false,
            menu_html = '',
            label = device.label;
        if (label.length > interface.split_numb){
            label = label.replace('Right', 'R');
            label = label.replace('Left', 'L');
            if (label.length > interface.split_numb){
                label = label.substr(0,interface.split_numb) + '...'
            }
        }
        if (device.device_type === "switch"){
            if (device.state.on === 1){
                on = true;
                state = 'On';
            } else {
                state = 'Off'
            }
            icon = '<img src="'+icon_svgs['lamp']+'">';

        } else if (device.device_type === 'rgb_light'){
            if (device.state.on){
                on = true;
                state = 'On';
            } else {
                state = 'Off'
            }
            icon = icon_svgs[device.product_name].replace('COLOR', "rgb("+device.state.red+", "+device.state.green+", "+device.state.blue+")")
        }
        devices_html += '<div'+(has_expand ? ' data-expand_id="'+expand_id.toString()+'"' : "")+' data-card_type="device" data-device_id="'+device.device_id+'" class="card simple'+(!on ? ' off' : '')+(has_expand ? ' expand' : "")+'">'+menu_html+icon+'<h1>'+label+'</h1><h2>'+state+'</h2></div>'

    }
    $(".row.devices").html(devices_html);
}

// updates the scenes from the array

function updateSceneSelection(scenes){
    let scenes_html = "<div data-scene_id='off' class='scene'>Off</div>";
    for (let i in scenes){
        let scene = scenes[i];
        scenes_html += '<div data-scene_id="'+scene.id+'" class="scene">'+scene.label+'</div>';
    }
    scenes_html += '<div class="slider"></div>';
    $(".scenes").html(scenes_html);
}

// updates the clicking event

function updateSceneSelector(currentScene=null){
    // scene click event
    $(".scene").click(function(){
        let scene_id = $(this).data("scene_id");
        updateScene(scene_id);
        setScene(scene_id); // calls the scene to the server
    });
    // loads the current scene if provided
    if (currentScene !== null){
        setTimeout(function () {
            updateScene(currentScene);
        }, 500);
    }

    // creates the click event for the add scene

    $("#create_scene").click(function () {
        let html = '' +
            '<div class="p">' +
                '<input placeholder="Scene Name" class="big" id="scene_name">' +
                '<label id="scene_id"></label>' +
                '<div class="device_status">loading...</div> ' +
                '<div class="button_row">' +
                    '<button class="main" id="create">' +
                        'Create' +
                    '</button>' +
                    '<button onclick="hideOverlay()">' +
                        'Cancel' +
                    '</button>' +
                '</div>' +
            '</div>';
        createOverlay("Create Scene", html, function(){
            let scene_name = $("#scene_name");
            scene_name.on('keydown keyup', function(){
                let name = $(this).val();
                name = name.toLowerCase();
                name = name.replace(/ /g, "_");
                $("#scene_id").html("Scene id: "+name);
            });
            $("#create").click(function () {
                let name = scene_name.val();
                name = name.toLowerCase();
                name = name.replace(/ /g, "_");
                apiRequest("rooms/"+interlock_content.current_room+"/create_scene/"+name, function(){
                    hideOverlay();
                });
            })
        });
    });

}

// updates the scene selector

function updateScene(scene_id){
    // defines some variables
    let button = $(".scene[data-scene_id='"+scene_id+"']"), // the button
        slider = $(".slider"), // the slider
        width = button.outerWidth(), // the width of the button
        left = button.position().left, // px from the left
        screenWidth = $("body").width(), // the body width
        sidebarWidthPercentage = .22; // the sidebar width percentage (22vw)

    $(".scene.active").removeClass("active"); // removes all active scenes classes

    button.addClass("active"); // adds the active class to the button

    slider.width(width); // sets the slider width to the button width

    left = left - (sidebarWidthPercentage * screenWidth); // calculates the width

    slider.css("margin-left", left+"px"); // sets the slider distance from the left
}

function setScene(scene_id){
    url = "rooms/"+interlock_content.current_room+"/"+scene_id;
    apiRequest(url, function(){
        console.log("success");
    })
}
// sets the interface theme
function setInterfaceTheme(theme){
    interface.theme = theme;
    let body = $("body"),
        theme_svg = null,
        theme_name = null;
    if (theme === "dark"){
        body.addClass("dark");
        theme_svg = "moon";
        theme_name = "Dark Theme";
    } else {
        body.removeClass("dark");
        theme_svg = "sun";
        theme_name = "Light Theme";
    }
    theme_html = icon_svgs[theme_svg]+theme_name;
    $(".theme_selector").html(theme_html);
}

$(document).ready(function(){
    // loads all the rooms from the server
    loadRooms();
    // sets the interface type
    setInterfaceTheme(interface.theme);
    // sets the click event
    $(".theme_selector").click(function(){
        let current_theme = interface.theme;
        if (current_theme === "dark"){
            setInterfaceTheme("light");
        } else {
            setInterfaceTheme("dark");
        }
    });
});